$(window).load(function () {
    $('.flexslider').flexslider({
        animation: "slide",
        slideshowSpeed: 10000,
        pauseOnAction: true,
        pauseOnHover: false,
        directionNav: true,
        controlNav: false,
        prevText: "Previous",
        nextText: "Next",
        slideshow: true,
        animation: 'slide',
        animationLoop: true
    });
});
